from tkinter import *from tkinter import  messageboximport mysql.connectorimport osmy_connect = mysql.connector.connect(  host="localhost",  user="lifechoices",  passwd="@Lifechoices1234",  database="lifechoicesonline")####### end of connection ####my_cursor = my_connect.cursor()def visit():    login_gui.withdraw()    import visitors.pydef showAdmin():  # Function to show admin    login_gui.destroy()    import admin_log.pydef login():    usr = username_ent.get()    p = password_ent.get()    sql = "select * from users where username=%s and password=%s"    my_cursor.execute(sql, [(usr), (p)])    datab = my_cursor.fetchall()    if datab:        messagebox.showinfo("Login", "login successful")        login_gui.destroy()        # import tkinter_gui.admin_rights_FE    else:        messagebox.showerror("Unsuccessful", "Login failed")def logout():    usr = username_ent.get()    p = password_ent.get()    sql = "select * from users where username=%s and password=%s"    my_cursor.execute(sql, [(usr), (p)])    datab = my_cursor.fetchall()    if datab:        messagebox.showinfo("Logout", "Logout succesfully")        login_gui.destroy()        # import tkinter_gui.admin_rights_FEdef clear():    fullname_ent.delete(0,END)    username_ent.delete(0,END)    password_ent.delete(0,END)# Start of the GUIlogin_gui = Tk()login_gui.geometry("520x300")login_gui.title("Login to Life Choices-Online")canvas = Canvas(login_gui, width = 520, height = 90,bg="black")canvas.pack()img = PhotoImage(file="index.png")canvas.create_image(5,5, anchor=NW, image=img)# FULL NAME# name labelfullname_lbl = Label(login_gui,text="Please enter your full name with a space: ")fullname_lbl.place(x=10,y=100)# fullname entry for usersfullname_ent = Entry(login_gui)fullname_ent.place(x= 290,y=100)# Usernamepassword_ent.pack()# username label for usersusername_lbl = Label(login_gui,text="Username: ")username_lbl.place(x=10,y=130)# username entry for usersusername_ent = Entry(login_gui)username_ent.place(x= 290,y=130)# PASSWORD# password label for userspassword_lbl = Label(login_gui,text="Please enter your password: ")password_lbl.place(x=10,y=160)# password entry for userspassword_ent = Entry(login_gui)password_ent.place(x= 290,y=160)# def on_record():#     messagebox.showinfo("Welcome", "You have \n successfully \n logged in.")# # Login Buttonlogin_btn = Button(login_gui,text="Log-in", command= login)login_btn.place(x=100, y=200)login_btn.config(width = 10)visitor_btn = Button(login_gui,text="Visitor",command=visit)visitor_btn.place(x=100, y=250)visitor_btn.config(width = 10)logout_btn = Button(login_gui,text="Log-out",command= logout)logout_btn.place(x=290, y=200)logout_btn.config(width = 10)clear_btn = Button(login_gui,text="Clear",command=clear)clear_btn.place(x=290, y=250)clear_btn.config(width = 10)login_gui.bind("<Control-a>", lambda i: showAdmin())login_gui.mainloop()