from tkinter import *from tkinter import  messageboximport mysql.connectorfrom datetime import *import osmydb = mysql.connector.connect(  host="localhost",  user="lifechoices",  password="@Lifechoices1234",)mycursor = mydb.cursor()# Create database if it does not existcursor = mydb.cursor()cursor.execute("create database if not exists lifechoicesonline")cursor.execute("use lifechoicesonline")# Creates the log in and log out tables if it doesn't exist in the databasecursor.execute("CREATE TABLE IF NOT EXISTS time_in(full_name varchar(60) Default null, date date, logged_in time)")mydb.commit()cursor.execute("CREATE TABLE IF NOT EXISTS time_out(full_name varchar(60) Default null, date date, logged_out time)")mydb.commit()def quit_login():    login_gui.withdraw()def show():    password_ent.config(show="")def visit():    login_gui.withdraw()    import visitors.pydef showAdmin():  # Function to show admin    login_gui.destroy()    import admin_log.pydef login():    usr = username_ent.get()    p = password_ent.get()    sql = "select * from users where username=%s and password=%s"    cursor.execute(sql, [(usr), (p)])    datab = cursor.fetchall()    if datab:        login1 = datetime.now()        y = login1.strftime("%H:%M:%S")        dt = login1.strftime("%d/%m/%y")        time1 = usr, str(dt),  str(y)        comm_time1 = "INSERT INTO time_in(full_name, date, logged_in)VALUES (%s, %s, %s)"        cursor.execute(comm_time1, time1)        mydb.commit()        messagebox.showinfo("SUCCESSFUL", "Logged in \n You logged in successfully")    else:        messagebox.showerror("Please note", "Login failed \n Please speak to the admin admin")        login_gui.withdraw()def logged_out():    usr = username_ent.get()    p = password_ent.get()    sql = "select * from users where username=%s and password=%s"    cursor.execute(sql, [(usr), (p)])    datab = cursor.fetchall()    messagebox.showinfo("SUCCESSFUL", "Logged out \n You logged out successfully")    if datab:        login1 = datetime.now()        y = login1.strftime("%H:%M:%S")        dt = login1.strftime("%d/%m/%y")        time1 = usr, str(dt),  str(y)        comm_time1 = "INSERT INTO time_out(full_name, date, logged_out)VALUES (%s, %s, %s)"        cursor.execute(comm_time1, time1)        mydb.commit()def clear():    fullname_ent.delete(0,END)    username_ent.delete(0,END)    password_ent.delete(0,END)# Start of the GUIlogin_gui = Tk()login_gui.geometry("520x300")login_gui.title("Login to Life Choices-Online")canvas = Canvas(login_gui, width = 520, height = 90,bg="black")canvas.pack()img = PhotoImage(file="index.png")canvas.create_image(5,5, anchor=NW, image=img)# FULL NAME# name labelfullname_lbl = Label(login_gui,text="Please enter your full name with a space: ")fullname_lbl.place(x=10,y=100)# fullname entry for usersfullname_ent = Entry(login_gui)fullname_ent.place(x= 290,y=100)# Usernamepassword_ent.pack()# username label for usersusername_lbl = Label(login_gui,text="Username: ")username_lbl.place(x=10,y=130)# username entry for usersusername_ent = Entry(login_gui)username_ent.place(x= 290,y=130)# PASSWORD# password label for userspassword_lbl = Label(login_gui,text="Please enter your password: ")password_lbl.place(x=10,y=160)# password entry for userspassword_ent = Entry(login_gui, show="*")password_ent.place(x= 290,y=160)# CHECKBUTTON THAT SHOW YOUR PASSWORD WHEN YOU CLICK ON ITshwPss = Checkbutton(login_gui, text="Show ", command=show)shwPss.place(x=210, y=160)login_btn = Button(login_gui,text="Log-in", command= login)login_btn.place(x=100, y=200)login_btn.config(width = 10)visitor_btn = Button(login_gui,text="Visitor",command=visit)visitor_btn.place(x=100, y=250)visitor_btn.config(width = 10)logout_btn = Button(login_gui,text="Log-out", command=logged_out)logout_btn.place(x=290, y=200)logout_btn.config(width = 10)clear_btn = Button(login_gui,text="Clear",command=clear)clear_btn.place(x=290, y=250)clear_btn.config(width = 10)quit_btn = Button(login_gui,text="Quit",command=quit_login)quit_btn.place(x=400, y=250)quit_btn.config(width = 10)login_gui.bind("<Control-a>", lambda i: showAdmin())login_gui.mainloop()